INSERT INTO `wp090901_posts` VALUES (251, 2, '2008-12-17 19:00:00', '2008-12-17 18:00:00', 'I should note that this is how NOT to peel an orange (or any other citrus fruit, for that matter). This happens to me almost every freaking time. Except for the screaming part, that just seemed funny to add.', '037 - Oranges', '', 'publish', 'open', 'open', '', '037-oranges', '', '', '2009-11-19 13:57:16', '2009-11-19 13:57:16', '', 0, 'http://www.mindbase21.be/comics/2008/12/17/037-oranges/', 0, 'webcomic_post', '', 1); 
