INSERT INTO `wp090901_posts` VALUES (261, 2, '2009-01-14 19:00:00', '2009-01-14 18:00:00', 'Products that offer some escape from reality will always do well, especially when times are though. And while I wouldn\'t want to take any of that away from anyone, I do wonder about backing an industry that makes turns a profit on the promise of helping people through hardship. Now more then ever, since <a href="http://news.bbc.co.uk/2/hi/health/7263494.stm" target="_blanc">studies have shown the effects of anti-depressants to be only slightly better than of placebo pills</a>.', '040 - Sell out', '', 'publish', 'open', 'open', '', '040-sell-out', '', '', '2009-11-19 13:59:35', '2009-11-19 13:59:35', '', 0, 'http://www.mindbase21.be/comics/2009/01/14/040-sell-out/', 0, 'webcomic_post', '', 0); 
