INSERT INTO `wp090901_posts` VALUES (291, 2, '2009-02-07 14:55:51', '2009-02-07 13:55:51', 'Remember <a href="http://www.mindbase21.be/siamese-twin-dolls-and-more/" target="_blank">those dolls I make</a>? They\'re starting to take up too much space in my little studio apartment. And since throwing them at people on the street proved to give too much trouble (you hit one little kid and all hell breaks loose, sheesh), I\'ve set up an online shop.\r\n<a href="http://www.mindbase21.be/shop">Get them right here.</a>\r\n\r\nUPDATE:\r\nI<del datetime="2010-09-28T11:25:15+00:00">\'ve moved the dolls over to <a href="http://mindbase21.etsy.com" target="_blank">my Etsy store.</a></del>\r\nVisit <a href="http://beardfluff.bigcartel.com">the beardfluff shop</a> for more stuff.', 'Online shop', '', 'publish', 'open', 'open', '', 'online-shop', '', '', '2010-09-28 13:27:02', '2010-09-28 11:27:02', '', 0, 'http://www.mindbase21.be/comics/?p=291', 0, 'post', '', 0); 
